package ch3;

public class op4 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a=10, b=20;
		
		System.out.println(a==10&&b==20);
		System.out.println(a>10&&b==20);
		System.out.println(a==10&&b==10);
		System.out.println(a<10&&b>20);
		System.out.println();	
		System.out.println(a==10||b==20);
		System.out.println(a>10||b==20);
		System.out.println(a==10||b==10);
		System.out.println(a<10||b>20);
		
		
		
	}

}
